mapper.select:
select m.order_num, m.genie_id, p.product_name product_name, m.recipient_name, m.recipient_phone, m.recipient_address, m.recipient_request, m.recipient_delivery_status, m.order_price, m.order_qty, m.order_writedate 
from myorder m join product p 
on m.product_id = p.product_id;

select product_id, genie_id, product_category, product_tag, product_name, product_price, product_quantity, product_mbti 
from product 

	where genie_id=#{genie_id} 


	where ${searchKey} like '%${searchWord}%' and genie_id=#{genie_id}

select genie_id, seller_tel, seller_email, seller_reg_no, company_name, ceo_name, seller_website
from seller 
where genie_id=#{genie_id}

select product_id, product_name, product_price, product_info, product_quantity, product_image1, product_image2, product_image3, product_category, product_tag, product_mbti 
from product
where product_id=${param1}

mapper.update:
update product set product_name=#{product_name}, product_price=${product_price}, product_info=#{product_info}, product_quantity=${product_quantity}, 
product_image1=#{product_image1}, product_image2=#{product_image2}, product_image3=#{product_image3}, product_category=#{product_category}, product_tag=#{product_tag}, product_mbti=#{product_mbti} 
where product_id=${product_id}
